// SolicitudDomicilios.jsx
import React, { useState } from "react";

function SolicitudDomicilios({ refId }) {
  const [codTipoDomicilio, setCodTipoDomicilio] = useState(0);
  const [tipoVia, setTipoVia] = useState("");
  const [nombreVia, setNombreVia] = useState("");
  const [numeroVia, setNumeroVia] = useState("");
  const [poblacion, setPoblacion] = useState("");
  const [codPostal, setCodPostal] = useState("");
  const [codPais, setCodPais] = useState("ES");
  const [telefono, setTelefono] = useState("");
  const [email, setEmail] = useState("");
  const [predeterminado, setPredeterminado] = useState(0);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      REF_ID: refId,   
      COD_TIPODOMICILIO: codTipoDomicilio,
      TIPO_VIA: tipoVia,
      NOMBRE_VIA: nombreVia,
      NUMERO_VIA: numeroVia,
      POBLACION: poblacion,
      COD_POSTAL: codPostal,
      COD_PAIS: codPais,
      TELEFONO: telefono,
      EMAIL: email,
      PREDETERMINADO: predeterminado,
    };

    try {
      const response = await fetch("http://127.0.0.1:5000/domicilios", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      const data = await response.json();
      alert(data.message || "Domicilio guardado correctamente");
    } catch (error) {
      console.error("❌ Error al enviar domicilio:", error);
      alert("Error al guardar domicilio");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Añadir Domicilio</h3>
      <input type="text" placeholder="Tipo de Vía" value={tipoVia} onChange={(e) => setTipoVia(e.target.value)} />
      <input type="text" placeholder="Nombre de Vía" value={nombreVia} onChange={(e) => setNombreVia(e.target.value)} />
      <input type="text" placeholder="Número de Vía" value={numeroVia} onChange={(e) => setNumeroVia(e.target.value)} />
      <input type="text" placeholder="Población" value={poblacion} onChange={(e) => setPoblacion(e.target.value)} />
      <input type="text" placeholder="Código Postal" value={codPostal} onChange={(e) => setCodPostal(e.target.value)} />
      <input type="text" placeholder="País" value={codPais} onChange={(e) => setCodPais(e.target.value)} />
      <input type="text" placeholder="Teléfono" value={telefono} onChange={(e) => setTelefono(e.target.value)} />
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <label>
        Predeterminado:
        <select value={predeterminado} onChange={(e) => setPredeterminado(Number(e.target.value))}>
          <option value={0}>No</option>
          <option value={1}>Sí</option>
        </select>
      </label>
      <button type="submit">Guardar Domicilio</button>
    </form>
  );
}

export default SolicitudDomicilios;
