import { useState } from "react";
import {
  Accordion,
  Button,
  Form,
  Row,
  Col,
  Card,
  Container,
} from "react-bootstrap";

function SociedadForm() {
  // ================== ESTADO PRINCIPAL ==================
  const [formData, setFormData] = useState({
    // Página 1: Solicitante
    razonSocial: "",
    nombreComercial: "",
    nif: "",
    formaJuridica: "",
    fechaConstitucion: "",
    registroMercantil: "",
    domicilio: "",
    localidad: "",
    provincia: "",
    cp: "",
    telefono: "",
    movil: "",
    email: "",
    web: "",
    numSeguridadSocial: "",
    representanteLegal: "",
    dniRepresentante: "",
    cargoRepresentante: "",
    personaContacto: "",
    telefonosContacto: "",

    // Página 2: Accionistas
    accionistas: [],

    // Página 3: Empresas vinculadas
    empresas: [],

    // Página 4: Operación
    tipoOperacion: "",
    tipoOperacionOtros: "",
    detalleOperacion: "",
    importeOperacion: "",
    plazoOperacion: "",
    entidadFinanciera: "",
    sucursalOperacion: "",

    // Página 5: Actividad y Garantías
    cnaePrincipal: "",
    descripcionActividad: "",
    locales: [],
    plantillaFijos: 0,
    plantillaEventuales: 0,
    plantillaAutonomos: 0,
    plantillaTotal: 0,
    prevision1: 0,
    prevision2: 0,
    prevision3: 0,
    garantiaHipotecaria: "",
    garantiaFiadores: "",
    garantiaOtras: "",
    comoConocio: "",

    // Página 6: Actividad general
    actividadPrincipal: "",
    cnae: "",
    fechaInicioActividad: "",
    empleados: "",
    facturacionAnual: "",
    sector: "",

    // Página 7: Observaciones
    observaciones: "",
    anexo1: "",
    anexo2: "",
    anexo3: "",
  });

  // ================== REPRESENTANTES ==================
  const [representantes, setRepresentantes] = useState([
    { nombre: "", dni: "", cargo: "", domicilio: "", telefono: "", email: "" },
  ]);

  // ================== HANDLERS ==================
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRepChange = (index, e) => {
    const newReps = [...representantes];
    newReps[index][e.target.name] = e.target.value;
    setRepresentantes(newReps);
  };

  const addRepresentante = () => {
    setRepresentantes([
      ...representantes,
      { nombre: "", dni: "", cargo: "", domicilio: "", telefono: "", email: "" },
    ]);
  };

  const removeRepresentante = (index) => {
    setRepresentantes(representantes.filter((_, i) => i !== index));
  };

  // Helpers para arrays (accionistas, empresas, locales)
  const addItem = (field) => {
    const newItem =
      field === "accionistas"
        ? { nombre: "", dni: "", importe: "", participacion: "" }
        : field === "empresas"
        ? { razonSocial: "", nif: "", participacion: "", trabajadores: "", grupo: "" }
        : field === "locales"
        ? { direccion: "", localidad: "", superficie: "", tipo: "", valor: "" }
        : {};
    setFormData({ ...formData, [field]: [...formData[field], newItem] });
  };

  const handleArrayChange = (e, index, field) => {
    const updated = [...formData[field]];
    updated[index][e.target.name] = e.target.value;
    setFormData({ ...formData, [field]: updated });
  };

  const updateRadioValue = (field, index, key, value) => {
    const updated = [...formData[field]];
    updated[index][key] = value;
    setFormData({ ...formData, [field]: updated });
  };

  // ================== GUARDAR Y PDF ==================
  const handleGuardar = async () => {
    const payload = { ...formData, representantes };
    await fetch("http://localhost:5000/sociedades", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });
    alert("Guardado en BBDD ✅");
  };

  const handleDescargarPDF = async () => {
    const payload = { ...formData, representantes };
    const response = await fetch("http://localhost:5000/sociedades/pdf", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });
    const blob = await response.blob();
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "solicitud_sociedades.pdf";
    a.click();
  };

  // ================== RENDER ==================
  return (
    <Container className="mt-4 p-4 bg-light rounded shadow">
      <h2 className="text-center text-primary mb-4">
        Solicitud de Sociedades
      </h2>

      <Accordion defaultActiveKey="0">
        {/* Aquí irían todos los Accordion.Item como en tu código */}
        {/* Te dejo el esqueleto listo */}
        <Accordion.Item eventKey="0">
          <Accordion.Header>Solicitante</Accordion.Header>
          <Accordion.Body>
            {/* ... tus campos de solicitante */}
          </Accordion.Body>
        </Accordion.Item>

        <Accordion.Item eventKey="1">
          <Accordion.Header>Accionistas</Accordion.Header>
          <Accordion.Body>
            {formData.accionistas.map((accionista, index) => (
              <div key={index} className="border p-3 mb-3 rounded">
                {/* Campos de accionista */}
              </div>
            ))}
            <Button variant="outline-primary" onClick={() => addItem("accionistas")}>
              + Añadir Accionista
            </Button>
          </Accordion.Body>
        </Accordion.Item>

        {/* Repite la misma estructura para empresas, operación solicitada, etc. */}
      </Accordion>

      <div className="mt-4 text-center">
        <Button variant="success" onClick={handleGuardar}>
          Guardar en BBDD
        </Button>
        <Button
          variant="secondary"
          className="ms-2"
          onClick={handleDescargarPDF}
        >
          Descargar PDF
        </Button>
      </div>
    </Container>
  );
}

export default SociedadForm;
