import { useState } from "react";
import { Accordion, Button, Form, Row, Col, Card } from "react-bootstrap";

function SolicitudSociedades() {
  const [formData, setFormData] = useState({
    razonSocial: "",
    nombreComercial: "",
    nif: "",
    formaJuridica: "",
    domicilio: "",
    localidad: "",
    provincia: "",
    cp: "",
    telefono: "",
    email: "",
    web: "",
    fechaConstitucion: "",
    registroMercantil: "",
    actividadPrincipal: "",
    cnae: "",
    fechaInicioActividad: "",
    empleados: "",
    facturacionAnual: "",
    sector: "",
    observaciones: "",
  });

  const [representantes, setRepresentantes] = useState([
    { nombre: "", dni: "", cargo: "", domicilio: "", telefono: "", email: "" },
  ]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleRepChange = (index, e) => {
    const newReps = [...representantes];
    newReps[index][e.target.name] = e.target.value;
    setRepresentantes(newReps);
  };

  const addRepresentante = () => {
    setRepresentantes([
      ...representantes,
      { nombre: "", dni: "", cargo: "", domicilio: "", telefono: "", email: "" },
    ]);
  };

  const removeRepresentante = (index) => {
    const newReps = representantes.filter((_, i) => i !== index);
    setRepresentantes(newReps);
  };

  const handleGuardar = async () => {
    const payload = { ...formData, representantes };
    await fetch("http://localhost:5000/sociedades", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });
    alert("Guardado en BBDD");
  };

  const handleDescargarPDF = async () => {
    const payload = { ...formData, representantes };
    const response = await fetch("http://localhost:5000/sociedades/pdf", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    });
    const blob = await response.blob();
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "solicitud_sociedades.pdf";
    a.click();
  };

  return (
    <div className="container mt-5">
      <Card className="shadow-lg border-0">
        <Card.Header className="text-center text-white" style={{ backgroundColor: "#004080" }}>
          <h3 className="mb-0">📑 Solicitud de Sociedades</h3>
        </Card.Header>
        <Card.Body>
          <Accordion defaultActiveKey="0" alwaysOpen>
            {/* Datos Generales */}
            <Accordion.Item eventKey="0">
              <Accordion.Header>Datos Generales</Accordion.Header>
              <Accordion.Body>
                {/* Campos principales */}
                <Form.Group className="mb-3">
                  <Form.Label>Razón Social</Form.Label>
                  <Form.Control
                    type="text"
                    name="razonSocial"
                    value={formData.razonSocial}
                    onChange={handleChange}
                  />
                </Form.Group>
              </Accordion.Body>
            </Accordion.Item>

            {/* Representantes dinámicos */}
            <Accordion.Item eventKey="1">
              <Accordion.Header>Representantes</Accordion.Header>
              <Accordion.Body>
                {representantes.map((rep, index) => (
                  <Card key={index} className="mb-3 border border-primary">
                    <Card.Body>
                      <h5 className="text-primary">Representante {index + 1}</h5>
                      <Row>
                        <Col md={6}>
                          <Form.Group className="mb-2">
                            <Form.Label>Nombre</Form.Label>
                            <Form.Control
                              type="text"
                              name="nombre"
                              value={rep.nombre}
                              onChange={(e) => handleRepChange(index, e)}
                            />
                          </Form.Group>
                        </Col>
                        <Col md={3}>
                          <Form.Group className="mb-2">
                            <Form.Label>DNI</Form.Label>
                            <Form.Control
                              type="text"
                              name="dni"
                              value={rep.dni}
                              onChange={(e) => handleRepChange(index, e)}
                            />
                          </Form.Group>
                        </Col>
                        <Col md={3}>
                          <Form.Group className="mb-2">
                            <Form.Label>Cargo</Form.Label>
                            <Form.Control
                              type="text"
                              name="cargo"
                              value={rep.cargo}
                              onChange={(e) => handleRepChange(index, e)}
                            />
                          </Form.Group>
                        </Col>
                      </Row>
                      <Row>
                        <Col md={6}>
                          <Form.Group className="mb-2">
                            <Form.Label>Domicilio</Form.Label>
                            <Form.Control
                              type="text"
                              name="domicilio"
                              value={rep.domicilio}
                              onChange={(e) => handleRepChange(index, e)}
                            />
                          </Form.Group>
                        </Col>
                        <Col md={3}>
                          <Form.Group className="mb-2">
                            <Form.Label>Teléfono</Form.Label>
                            <Form.Control
                              type="text"
                              name="telefono"
                              value={rep.telefono}
                              onChange={(e) => handleRepChange(index, e)}
                            />
                          </Form.Group>
                        </Col>
                        <Col md={3}>
                          <Form.Group className="mb-2">
                            <Form.Label>Email</Form.Label>
                            <Form.Control
                              type="email"
                              name="email"
                              value={rep.email}
                              onChange={(e) => handleRepChange(index, e)}
                            />
                          </Form.Group>
                        </Col>
                      </Row>
                      {representantes.length > 1 && (
                        <Button
                          variant="outline-danger"
                          size="sm"
                          className="mt-2"
                          onClick={() => removeRepresentante(index)}
                        >
                          ❌ Eliminar
                        </Button>
                      )}
                    </Card.Body>
                  </Card>
                ))}

                <Button variant="outline-primary" onClick={addRepresentante}>
                  ➕ Añadir representante
                </Button>
              </Accordion.Body>
            </Accordion.Item>
          </Accordion>

          <div className="text-center mt-4">
            <Button variant="success" size="lg" className="me-2" onClick={handleGuardar}>
              💾 Guardar en BBDD
            </Button>
            <Button variant="primary" size="lg" onClick={handleDescargarPDF}>
              📥 Descargar PDF
            </Button>
          </div>
        </Card.Body>
      </Card>
    </div>
  );
}

export default SolicitudSociedades;
